import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const validateEmail = (email) => /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);

  const handleSubmit = (e) => {
    e.preventDefault();
    // for Passsword check is 
    if (!validateEmail(email)) {
      setError('Please enter a valid email.');
    } else if (password.length < 8) {
      setError('Password must be at least 8 characters.');
    } else {
      setError('');
      // Dummy successful login -> go to dashboard
      navigate('/dashboard');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-3">
      <h2 className="text-lg font-medium text-center text-[#5B21B6]">Login</h2>

      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        className="w-full border border-gray-200 p-3 rounded-lg focus:outline-none focus:ring-2 focus:ring-[#C7B3F5]"
      />

      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        className="w-full border border-gray-200 p-3 rounded-lg focus:outline-none focus:ring-2 focus:ring-[#C7B3F5]"
      />

      {error && <p className="text-red-500 text-sm">{error}</p>}

      <button className="w-full bg-gradient-to-r from-[#A78BFA] to-[#93C5FD] text-white py-2 rounded-lg hover:opacity-95 transition">
        Login
      </button>

      <p className="text-center text-sm text-gray-600">
        Don't have an account? <Link to="/signup" className="text-[#6B21A8] hover:underline">Signup</Link>
      </p>
    </form>
  );
}

export default Login;
